document.import=function(){var ajax=new function(){this.x=function(){for(a=0;a<4;a++){try{return a?new ActiveXObject([,"Msxml2","Msxml3","Microsoft"][a]+".XMLHTTP"):new XMLHttpRequest}catch(e){}}};this.get=function(e,t,n){var r=new this.x;r.open("GET",e,true);r.send();r.onreadystatechange=function(){if(r.readyState==4){if(r.status==200){if(t&&typeof t=="function"){t(r.responseText)}}else{if(n&&typeof n=="function"){n(r)}}}}};this.getHTML=function(container,url){this.get(url,function(r){container.innerHTML=r;var scripts=container.querySelectorAll("script");for(var j=0;j<scripts.length;j++){if(scripts[j].innerHTML)eval(scripts[j].innerHTML);if(scripts[j].src)ajax.getScript(scripts[j].src)}container.removeAttribute("data-import");if(document.createEventNodeList){container.fireEvent("loaded",document.createEventNodeList())}else{var evt=document.createEvent("HTMLEvents");evt.initEvent("loaded",true,true);container.dispatchEvent(evt)}})};this.getScript=function(url){this.get(url,function(r){eval(r)})}};var container=this.querySelectorAll("[data-import]");for(var i=0;i<container.length;i++){var src=container[i].getAttribute("data-import");ajax.getHTML(container[i],src)}};document.addEventListener("DOMContentLoaded",function(){this.import()})